import React, { useState } from 'react';

const Rating = ({ rating }) => {
  const [starsVisibility, setStarsVisibility] = useState(Array(8).fill(false));

  // Update stars visibility based on the rating
  useState(() => {
    const updatedStarsVisibility = Array(8).fill(false);
    for (let i = 0; i < rating; i++) {
      updatedStarsVisibility[i] = true;
    }
    setStarsVisibility(updatedStarsVisibility);
  }, [rating]);

  return (
    <div>
      {starsVisibility.map((isVisible, index) => (
        <div className="star" key={index}>
          {isVisible ? '★' : '☆'}
        </div>
      ))}
    </div>
  );
};

export default Rating;
